package sist.com.thread1;

public class StringBar {
	private String msg;
	private int x, y;
	private String color;
	
	public StringBar() {
		super();
		// TODO Auto-generated constructor stub
	}

	public StringBar(String msg, int x, int y, String color) {
		super();
		this.msg = msg;
		this.x = x;
		this.y = y;
		this.color = color;
	}

	public String getMsg() {
		return msg;
	}

	public void setMsg(String msg) {
		this.msg = msg;
	}

	public int getX() {
		return x;
	}

	public void setX(int x) {
		this.x = x;
	}

	public int getY() {
		return y;
	}

	public void setY(int y) {
		this.y = y;
	}

	public String getColor() {
		return color;
	}

	public void setColor(String color) {
		this.color = color;
	}
	
	
	
}
